
describe('setImmediate shim loading', function () {

    it('should have been defined?', function () {
        if(typeof window.setImmediate !== 'function') {
            throw new Error('setImmediate shim not loaded!');
        }
    });

    it('should have been defined (delayed)', function (done) {
        setTimeout(function() {
        if(typeof window.setImmediate !== 'function') {
            done(new Error('setImmediate shim not loaded!'));
        }
        done();
        }, 1000);
    });

    it('should work', function (done) {
        setImmediate(function() {
            done();
        });
    });
});
